const http = require("http");
const https = require("https");

const UPSTREAM_HOST = "duckduckgo.com";
const UPSTREAM_ORIGIN = "https://duckduckgo.com";

function rewriteLocation(loc) {
  if (!loc) return loc;
  if (loc.startsWith(UPSTREAM_ORIGIN)) return loc.slice(UPSTREAM_ORIGIN.length) || "/";
  return loc;
}

const server = http.createServer((req, res) => {
  const options = {
    hostname: UPSTREAM_HOST,
    port: 443,
    path: req.url,
    method: req.method,
    headers: { ...req.headers, host: UPSTREAM_HOST },
  };

  const upstreamReq = https.request(options, (upstreamRes) => {
    const headers = { ...upstreamRes.headers };
    if (headers.location) headers.location = rewriteLocation(headers.location);

    res.writeHead(upstreamRes.statusCode || 500, headers);
    upstreamRes.pipe(res);
  });

  upstreamReq.on("error", () => {
    res.writeHead(502, { "content-type": "text/plain" });
    res.end("Bad Gateway");
  });

  req.pipe(upstreamReq);
});

server.listen(process.env.PORT || 8080);
